events.inlineCode:
gdjs._extensionInternetConnectivity = {
  Status: false,
  UrlBasis: 'https://example.com/',
  FrequencyCheckSeconds: 5
};


// Checks internet via ping method
const CheckOnlineStatus = async () => {
  try {
    const online = await fetch(gdjs._extensionInternetConnectivity.UrlBasis, {
      method: 'GET',
      mode: 'no-cors',
      cache: 'no-cache',
    });

    return online.status == 0;
  } catch (e) {
    return false;
  }
};

// Check internet if browser/device detects an event
window.addEventListener('load', DetectInternet);
window.addEventListener('online', DetectInternet);
window.addEventListener('offline', DetectInternet);

// Update Status in a variable
async function DetectInternet() {
  if(await CheckOnlineStatus()) {
      gdjs._extensionInternetConnectivity.Status = true;
  } else {
      gdjs._extensionInternetConnectivity.Status = false;
  }

  setTimeout(DetectInternet, gdjs._extensionInternetConnectivity.FrequencyCheckSeconds*1000);
}

DetectInternet();

// Update Url Settings
gdjs._extensionInternetConnectivity.UrlBasis = eventsFunctionContext.getArgument('Url').toString();

// Update Frequency Settings
gdjs._extensionInternetConnectivity.FrequencyCheckSeconds = eventsFunctionContext.getArgument('Seconds').valueOf();
