const priorityComparator = (a,b) => b.priority - a.priority;
const operationComparator = (a,b) => operationMap[a.operation].order - operationMap[b.operation].order;
const operationMap = {
  '=':{
        order: 0,
        apply:(a,b) => b,
      },
  '*':{
        order: 1,
        apply:(a,b) => a*b,
      },
  '/':{
        order: 2,
        apply:(a,b) => a/b,
      },
  '-':{
        order: 3,
        apply:(a,b) => a-b,
      },
  '+':{
        order: 4,
        apply:(a,b) => a+b,
      },
};

prototype.stats = {};
prototype.buffs = {};
prototype.abilities = {};
prototype.MANUAL ="manual";

prototype.setStat = function (name, amount, operation){
  let newStat = {};
  //get the stat if it exists
  if(this.stats[name]){
    newStat = this.stats[name];
  }
  //apply the operation to the stat
  if(operationMap[operation]){
    newStat.baseValue = operationMap[operation].apply(newStat.baseValue, amount);
  }
  //if the stat has no value, give it a value
  if(newStat.value == undefined){
    newStat.value = newStat.baseValue;
  }
  //save the new stat
  //Note: buff operations are not recalculated, 
  //that will happen on the next frame
  this.stats[name] = newStat;
}

prototype.updateBuffs = function(deltaTime){
  let buffNames = Object.keys(this.buffs);
  let abilities = [];
  let statChanges = [];

  //iterate over the buffs 
  for (let buffName of buffNames){
    let buff = this.buffs[buffName];
    if(buff.remainingTime <= 0 || buff.stackCount <= 0){
      //remove those without stacks or time left
      delete this.buffs[buffName];
    }else{
      //update remaining time on others
      buff.remainingTime -= deltaTime;
      //add the abilities to the pool
      abilities = abilities.concat(buff.abilities);
      // add their operators to the stat calculator
      statChanges = statChanges.concat(buff.statChanges);
    }
  }
  this.updateAbilities(abilities, deltaTime);
  this.updateStats(statChanges);
}

prototype.updateStats = function(statChanges){
  //reset stats to their base value
  this.resetStats();
  //sort to apply changes in the correct sequence
  statChanges.sort(operationComparator);
  statChanges.sort(priorityComparator);

  for(let change of statChanges){
    //check if the stat exists
    let stat = this.stats[change.name];
    if(stat && operationMap[change.operation]){
      stat.value = operationMap[change.operation].apply(stat.value, change.amount);
    }
  }
}

prototype.resetStats = function(){
  for(let statName in this.stats){
    this.stats[statName].value = this.stats[statName].baseValue;
  }
}

prototype.updateAbilities = function (abilitiesByBuffs, deltaTime){
  let abilityNames = Object.keys(this.abilities);

  //update existing abilities
  for (let abilityName of abilityNames){
    let ability = this.abilities[abilityName];
    //update those abilities with time remaining
    if(ability.remainingTime > 0){
      ability.remainingTime -= deltaTime;
    }else if(ability.source != this.MANUAL || ability.remainingTime <= 0) {
      //remove expired abilties and those granted by buffs
      delete this.abilities[abilityName];
    }
  }
  //re-add the abilities granted by active buffs
  for(let ability of abilitiesByBuffs){
    //don't overwrite abilities, they may include duration data
    if(!this.abilities[ability.name]){
      this.abilities[ability.name] = ability;
    }
  }
}

prototype.addBuff = function (buffBehavior, duration){
  const name = buffBehavior.BuffName();
  let remainingTime;

  if(duration < 0){
    duration = buffBehavior.Duration();
  }
  if (duration > 0){
    remainingTime = duration*1000;
  }
  // if the buff is already present, update it
  if(this.buffs[name]){
    const buff = this.buffs[name];
    // if refreshable, extend the remaining time
    if(buffBehavior.Refreshable()){
      buff.remainingTime = remainingTime;
    }
    //if stackable, incrase the stack count
    if(buffBehavior.Stackable()){
      buff.stackCount += 1;
      const stackLimit = buffBehavior.StackLimit();
      //if defined, apply limit
      if(stackLimit > 0 && buff.stackCount > stackLimit){
        buff.stackCount = stackLimit;
      }
      //update the stats to the new stack count
      buff.statChanges = buffBehavior.getStatChangesArray(buff.stackCount);
    }
  }else{
    //add the new buff
    this.buffs[name] = {
      abilities : buffBehavior.getAbilityArray(),
      statChanges: buffBehavior.getStatChangesArray(),
      remainingTime,
      stackCount: 1,
    };
  }
};

prototype.removeBuff = function (buffBehavior, stackOnly){
  const name = buffBehavior.BuffName();
  const buff = this.buffs[name];

  if(buff == undefined){
    return;
  }
  if(stackOnly){
     buff.stackCount--;
     buff.statChanges = buffBehavior.getStatChangesArray(buff.stackCount);
  }else{
     buff.remainingTime = 0;
  }
}

prototype.addAbility = function (abilityName, duration){
  let ability = { source: this.MANUAL };
  if (duration > 0){
    ability.remainingTime = duration*1000;
  }
  ability.duration = duration;
  this.abilities[abilityName] = ability;
}

prototype.removeAbility = function (abilityName, duration){
  delete this.abilities[abilityName];
}

prototype.hasAbility = function (abilityName){
  return this.abilities[abilityName] != undefined;
}

prototype.hasBuff = function (buffBehavior){
  return this.buffs[buffBehavior.BuffName()] != undefined;
}

prototype.getStackCount = function (buffBehavior){
  let returnValue = 0;
  const buff = this.buffs[buffBehavior.BuffName()]
  if(buff){
      returnValue = buff.stackCount;
  }
  return returnValue;
}

prototype.getRemainingTime = function (buffBehavior){
  let returnValue = 0;
  const buff = this.buffs[buffBehavior.BuffName()];
  if(buff){
      returnValue = buff.remainingTime;
  }
  return returnValue;
}

