/* These property getters are dynamic, meaning they can support
up to 10 abilities and stat changes without changing the code,
simply by adding them as behavior properties.
*/

prototype.getAbilityArray = function (){
    let abilities = [];
    for(let i=1; i<10; i++){
        //check if property number has been defined in behavior
        if(this["_getAbility"+i]){
            const name = this["_getAbility"+i]();
            //check if property has been provided
            if(name){
                abilities.push({ name, source: this.BuffName()});
            }
        }else{
            //no further properties were defined
            break;
        }
    }
    return abilities;
}

prototype.getStatChangesArray = function (stackCount){
    let statChanges = [];
    for(let i=1; i<10; i++){
        //check if property number has been defined in behavior
        if(this["_getStat"+i+"Name"]){
            const name = this["_getStat"+i+"Name"]();
            // check if property has been provided
            if(name){
                const operation = this["_getStat"+i+"Operation"]().substring(0,1);
                const priority = this["_getStat"+i+"Priority"]();
                let amount = this["_getStat"+i+"Amount"]();

                //this is not the proper way to deal with stacks
                //but it's more efficient than cycling
                if(stackCount && operation != "="){
                    amount *= stackCount;
                }

                statChanges.push({ name, operation, amount, priority });
            }
        }else{
            //no further properties were defined
            break;
        }
    }
    return statChanges;
}

