events.inlineCode:
window.__extensionGooglePlayGamesService = {
    API: {
        Android: null
    },
    AndroidReady: false
};

document.addEventListener('deviceready', ()=>{
    window.__extensionGooglePlayGamesService.AndroidReady = true;
    window.__extensionGooglePlayGamesService.API.Android = cordova.plugins.playGamesServices;
}, false);

const Method = eventsFunctionContext.getArgument('Method');
const ResultSceneVariable = eventsFunctionContext.getArgument('ResultSceneVariable');
const ParameterSceneVariable = eventsFunctionContext.getArgument('ParameterSceneVariable');

const Parameters = runtimeScene.getVariables().get(ParameterSceneVariable).toJSObject();

window.__extensionGooglePlayGamesService.API.Android[Method](Parameters, () => {
    runtimeScene.getVariables().get(ResultSceneVariable).fromJSObject(arguments);
},() => {
    runtimeScene.getVariables().get(ResultSceneVariable).fromJSObject(arguments);
});
